class Header{constructor(){this.burger=document.querySelector(".header__burger"),this.nav=document.querySelector(".header__nav"),this.body=document.body,this.init()}init(){this.bindEvents()}bindEvents(){this.burger.addEventListener("click",()=>{this.toggleMenu()}),this.nav.addEventListener("click",e=>{e.target.classList.contains("nav__link")&&this.closeMenu()}),document.addEventListener("click",e=>{!e.target.closest(".header")&&this.nav.classList.contains("active")&&this.closeMenu()}),window.addEventListener("resize",()=>{768<window.innerWidth&&this.closeMenu()})}toggleMenu(){this.burger.classList.toggle("active"),this.nav.classList.toggle("active"),this.body.classList.toggle("menu-open")}closeMenu(){this.burger.classList.remove("active"),this.nav.classList.remove("active"),this.body.classList.remove("menu-open")}}document.addEventListener("DOMContentLoaded",()=>{console.log("Header initialized"),new Header}),document.addEventListener("DOMContentLoaded",function(){console.log("Магазин инициализирован")});